INSERT INTO `categories` (`id`, `name`, `description`, `created_at`, `updated_at`) VALUES
	(1, 'Leather Footballs', '<h2>Leather Footballs</h2>', '2017-05-13 19:16:34', '2017-05-13 19:16:34'),
	(2, 'Baseballs', '<p>Baseballs</p>', '2017-05-14 18:59:57', '2017-05-14 19:06:23'),
	(3, 'Basketballs', '<h1 class="collection-title" style="font-weight: 400; color: #333333; margin: 0px 0px 13px; text-transform: none; font-family: Asap, \'Lucida Grande\', Helvetica, Arial, sans-serif; font-size: 36px; line-height: 36px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial;">Leather Basketballs</h1>\r\n<p><span style="color: #555555; font-family: Georgia, Utopia, \'Times New Roman\', Times, serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial; display: inline !important; float: none;">LEATHER HEAD&trade; Handsome American Basketballs.</span></p>', '2017-05-13 19:17:31', '2017-05-13 19:17:31'),
	(4, 'Soccer/Rugby/Fitness', '<h2>Soccer/Rugby/Fitness</h2>', '2017-05-13 20:44:36', '2017-05-13 20:44:36'),
	(5, 'Gloves', '<div class="clearfix" style="zoom: 1; color: #555555; font-family: Georgia, Utopia, \'Times New Roman\', Times, serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial;">\r\n<h1 class="collection-title" style="font-weight: 400; color: #333333; margin: 0px 0px 13px; text-transform: none; font-family: Asap, \'Lucida Grande\', Helvetica, Arial, sans-serif; font-size: 36px; line-height: 36px; float: left;">Leather Baseball Gloves</h1>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n</div>\r\n<h6 style="font-weight: 400; color: #333333; margin: 0px 0px 13px; text-transform: uppercase; font-family: Asap, \'Lucida Grande\', Helvetica, Arial, sans-serif; font-size: 10px; line-height: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial;">AT LEATHER HEAD BASEBALL WE HAVE A SIMPLE GOAL: CREATE GLOVES FOR MAJOR LEAGUE PLAYERS.</h6>\r\n<h6 style="font-weight: 400; color: #333333; margin: 0px 0px 13px; text-transform: uppercase; font-family: Asap, \'Lucida Grande\', Helvetica, Arial, sans-serif; font-size: 10px; line-height: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial;">WE AUDITIONED A FEW OF THE WORLD&rsquo;S MOST HIGHLY REGARDED BASEBALL GLOVE FACTORIES. WHEN WE FOUND OUR MATCH, WE ASKED THEM TO CREATE A HANDFUL OF DESIGNS WITH THE FOLLOWING SPECS:<br /><strong style="font-weight: bold;">1.</strong>MAKE THE BEST GLOVES FOR MAJOR LEAGUE CALIBER PLAYERS.&nbsp; GLOVES THAT TAKE TIME TO BREAK IN, BUT THAT WILL ALSO STAND UP TO THE RIGORS OF MULTIPLE MAJOR LEAGUE SEASONS.<br /><strong style="font-weight: bold;">2.</strong><span class="Apple-converted-space">&nbsp;</span>USE THE BEST LEATHER, REGARDLESS OF COST.<br /><strong style="font-weight: bold;">3.</strong><span class="Apple-converted-space">&nbsp;</span>ALL LEATHER COMPONENTS OF THE GLOVE MUST COME FROM THE SAME TANNAGE OF LEATHER. WHY?&nbsp; BECAUSE WHEN THE ENTIRE GLOVE IS MADE FROM THE SAME LEATHER THE COMPONENTS MARRY INTO A SINGLE FLAWLESS UNIT.<br /><strong style="font-weight: bold;">4.</strong><span class="Apple-converted-space">&nbsp;</span>USE THE BEST COMPONENTS, INCLUDING:<br /><strong style="font-weight: bold;">A.</strong><span class="Apple-converted-space">&nbsp;</span>WELTING: CLARINO.&nbsp; CLARINO IS AN INDESTRUCTIBLE MICROFIBER.&nbsp; IT MAKES SUPERIOR WELTING, RESULTING IN A STIFFER, MORE DURABLE GLOVE.<br /><strong style="font-weight: bold;">B.</strong><span class="Apple-converted-space">&nbsp;</span>FELT: THICK, DENSE FELT IS OFTEN WHAT DISTINGUISHED A STURDY, LONG LASTING GLOVE FROM ONE THAT GOES FLOPPY AFTER A WEEK OF PLAY.<br /><strong style="font-weight: bold;">C.</strong><span class="Apple-converted-space">&nbsp;</span>REINFORCEMENTS: RESEARCH TELLS US THAT ELITE PLAYERS PREFER THEIR GLOVES WITH A STIFF PINKIE AND THUMB.<br /><strong style="font-weight: bold;">D.</strong><span class="Apple-converted-space">&nbsp;</span>LACE: TENNESSEE TANNING LACE IS THE BEST THERE IS.&nbsp; THIS IS THE STUFF WE WANT IN OUR GLOVES.<br /><strong style="font-weight: bold;">5.</strong><span class="Apple-converted-space">&nbsp;</span>MAKE THESE GLOVES ON THE FACTORY&rsquo;S MOST EXPERIENCE PRODUCTION LINE. EACH PIECE OF THE GLOVE MUST COME TOGETHER WITH EXACTING PRECISION.&nbsp; IT TAKES YEARS OF PRACTICE AND EXPERIENCE TO SEW A MAJOR LEAGUE QUALITY GLOVE. LEATHER HEAD BASEBALL GLOVES REPRESENT THE BEST GLOVE THAT A CRAFTSMAN CAN PRODUCE.</h6>\r\n<h6 style="font-weight: 400; color: #333333; margin: 0px 0px 13px; text-transform: uppercase; font-family: Asap, \'Lucida Grande\', Helvetica, Arial, sans-serif; font-size: 10px; line-height: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial;">FINALLY, WE PUT THE GLOVES INTO THE HANDS OF EXPERIENCED MAJOR LEAGUE PLAYERS AND ELITE COLLEGIATE PLAYERS.&nbsp; THEY WERE TOLD TO BEAT THEM UP AND COME BACK WITH CONSTRUCTIVE CRITICISM.&nbsp; FEEDBACK WAS OFFERED AND TWEAKS WERE MADE.<br />THE<span class="Apple-converted-space">&nbsp;</span><strong style="font-weight: bold;">LEATHER HEAD SPORTS</strong><span class="Apple-converted-space">&nbsp;</span>BRAND REPRESENTS FINE LEATHER AND EXCELLENCE IN CRAFTSMANSHIP.&nbsp; IT REPRESENTS NOSTALGIA.&nbsp;&nbsp;&nbsp; LEATHER HEAD BASEBALL GLOVES ARE DESIGNED TO EVOKE MEMORIES OF THE BEST BASEBALL GLOVES FROM 30 YEARS AGO; GLOVES WITHOUT BELLS AND WHISTLES.</h6>\r\n<h6 style="font-weight: 400; color: #333333; margin: 0px 0px 13px; text-transform: uppercase; font-family: Asap, \'Lucida Grande\', Helvetica, Arial, sans-serif; font-size: 10px; line-height: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial;">WITH AESTHETICALLY CLEAN AND MODERN DESIGNS, WE ALSO KNEW WE NEEDED GREAT GRAPHICS TO COMPLIMENT THE GLOVES.&nbsp; FOR THIS WE TURNED TO THE EXTRAORDINARILY TALENTED<span class="Apple-converted-space">&nbsp;</span><a style="color: #333333; text-decoration: none; outline: none;" href="http://joncontino.com/" target="_blank" rel="noopener noreferrer">JON CONTINO</a><span class="Apple-converted-space">&nbsp;</span>TO CREATE A WHOLE PACKET OF&nbsp; LEATHER HEAD BRANDING THAT WOULD SPEAK TO OUR HERITAGE AND GIVE THE GLOVES A MODERN BUT CLASSIC APPEAL. WE SPARED NO EXPENSE TO CREATE A BASEBALL GLOVE THAT IS TRUE TO THE LEATHER HEAD NAME.</h6>', '2017-05-13 20:45:05', '2017-05-13 20:45:05'),
	(6, 'Custom Balls', '<p style="margin: 0px 0px 20px; color: #555555; font-family: Georgia, Utopia, \'Times New Roman\', Times, serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial;">What makes Leather Head Sports different and special is our ability to do one-offs and small runs of highly customized, individually personalized sports balls. &nbsp;We love working with our customers on special projects, so give us a call if you\'re looking for a gift that will blow them away.</p>\r\n<p style="margin: 0px 0px 20px; color: #555555; font-family: Georgia, Utopia, \'Times New Roman\', Times, serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial;">Many of the items in our shop can be engraved with a Name, Initials, Personal message, and even Logos.<br />Perfect for Groomsman\'s gifts, birthdays, graduations, and sports awards.</p>\r\n<p style="margin: 0px 0px 20px; color: #555555; font-family: Georgia, Utopia, \'Times New Roman\', Times, serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial;">For basic personalization, use our<span class="Apple-converted-space">&nbsp;</span><a style="color: #130ce3; text-decoration: none; outline: none;" href="http://www.leatherheadsports.com/collections/7-custom-engraving-service/products/custom-engraving-service" target="_blank" rel="noopener noreferrer">simple form</a><span class="Apple-converted-space">&nbsp;</span>to send your engraving instructions.<br />For more elaborate engraving instructions, we recommend that you contact us directly.<br /><a style="color: #130ce3; text-decoration: none; outline: none;" href="http://www.leatherheadsports.com/pages/contact-us" target="_blank" rel="noopener noreferrer">Send an e-mail with the details of your engraving to:<span class="Apple-converted-space">&nbsp;</span></a><br /><strong style="font-weight: bold;">Paul.Cunningham@LeatherHeadSports.com</strong><span class="Apple-converted-space">&nbsp;</span><br />Paul will respond to confirm the details of your engraving request, or you can start a dialog to ensure that you get exactly what you want.</p>\r\n<p style="margin: 0px 0px 20px; color: #555555; font-family: Georgia, Utopia, \'Times New Roman\', Times, serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial;"><strong style="font-weight: bold;">COST:<span class="Apple-converted-space">&nbsp;</span></strong>$25.00 per panel</p>\r\n<h3 style="font-weight: 400; color: #333333; margin: 0px 0px 13px; text-transform: none; font-family: Asap, \'Lucida Grande\', Helvetica, Arial, sans-serif; font-size: 24px; line-height: 28px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial;"><a style="color: #333333; text-decoration: none; outline: none;" title="Custom Engraving" href="http://www.leatherheadsports.com/collections/custom-engraving-service/" target="_blank" rel="noopener noreferrer"><span style="color: #0000ff;">CLICK HERE TO ORDER CUSTOM ENGRAVING</span></a></h3>\r\n<p style="margin: 0px 0px 20px; color: #555555; font-family: Georgia, Utopia, \'Times New Roman\', Times, serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial;"><strong style="font-weight: bold;">TIMING:</strong><span class="Apple-converted-space">&nbsp;</span>Engraved balls are made to order.&nbsp; Turnaround time is 1-2&nbsp;weeks.</p>\r\n<p style="margin: 0px 0px 20px; color: #555555; font-family: Georgia, Utopia, \'Times New Roman\', Times, serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial;"><strong style="font-weight: bold;">NOTES:&nbsp;</strong></p>\r\n<p style="margin: 0px 0px 20px; color: #555555; font-family: Georgia, Utopia, \'Times New Roman\', Times, serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial;"><strong style="font-weight: bold;">Limitations:</strong>&nbsp;&nbsp;<strong style="font-weight: bold;">Lemon Balls</strong><span class="Apple-converted-space">&nbsp;</span>have limited space for engraving. &nbsp;We recommend two lines of text with a maximum of six characters per line. &nbsp;<strong style="font-weight: bold;">Footballs</strong><span class="Apple-converted-space">&nbsp;</span>have a blank panel with engravable space of 4\' x 2.5". &nbsp;Also the football can accommodate two lines of text below the air valve. &nbsp;There is a character limit, so please inquire with your proposed message. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\r\n<p style="margin: 0px 0px 20px; color: #555555; font-family: Georgia, Utopia, \'Times New Roman\', Times, serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial;"><strong style="font-weight: bold; line-height: 1.5;">Fonts:</strong><span style="line-height: 1.5;"><span class="Apple-converted-space">&nbsp;</span>We typically use Times Roman, however if there is a font you prefer you may request that. &nbsp;</span>We will do our best to accommodate your request.</p>\r\n<p style="margin: 0px 0px 20px; color: #555555; font-family: Georgia, Utopia, \'Times New Roman\', Times, serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial;"><br /><strong style="font-weight: bold;">The Process:<span class="Apple-converted-space">&nbsp;</span></strong>Custom engraving techniques include Laser engraving or Heat embossing.&nbsp; Either option has its\' Pros and Cons.<br />We welcome the opportunity to consult with you to insure the best results for your project.</p>\r\n<p style="margin: 0px 0px 20px; color: #555555; font-family: Georgia, Utopia, \'Times New Roman\', Times, serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial;"><br /><strong style="font-weight: bold;">Logos:</strong><span class="Apple-converted-space">&nbsp;</span>If submitting logos, they must be high resolution (300dpi) B+W&nbsp; jpeg files.&nbsp; Colors or shades of gray are not engravable.</p>\r\n<p style="margin: 0px 0px 20px; color: #555555; font-family: Georgia, Utopia, \'Times New Roman\', Times, serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial;"><strong style="font-weight: bold;">Questions:</strong><span class="Apple-converted-space">&nbsp;</span>&nbsp;Call 201-857-0443</p>', '2017-05-13 20:47:48', '2017-05-13 20:47:48'),
	(7, 'Apparel', '<h1 class="collection-title" style="font-weight: 400; color: #333333; margin: 0px 0px 13px; text-transform: none; font-family: Asap, \'Lucida Grande\', Helvetica, Arial, sans-serif; font-size: 36px; line-height: 36px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial;">Leather Head Apparel</h1>\r\n<p><span style="color: #555555; font-family: Georgia, Utopia, \'Times New Roman\', Times, serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial; display: inline !important; float: none;">Support Team Leather Head with apparel bearing our Jon Contino designed logo art.</span></p>', '2017-05-13 20:48:15', '2017-05-13 20:48:15'),
